data.query:
UPDATE baseball.accum_record_hitter
SET RESULT = RESULT + 1
WHERE hitter = '{hitter}'
{state}
AND gyear IN (2017, 'NA')
AND state_split in ('BASIC', '{score}', '{base}');

UPDATE baseball.accum_record_hitter
SET RESULT = RESULT + 1
WHERE hitter = 76249
{state}
AND gyear IN (2017, 'NA')
AND pitteam = '{pitteam}'
AND state_split = 'VERSUS_TEAM'
AND opponent = 'TEAM';

UPDATE baseball.accum_record_hitter
SET RESULT = RESULT + 1
WHERE hitter = '{hitter}'
{state}
AND gyear = 'NA'
AND pitcher = '{pitcher}'
AND state_split = 'VERSUS_PITCHER'
AND opponent = 'PITCHER';

COMMIT;
